  //일단 게시글만.
  return (
    <Form onFinish={onSubmit}>
      <Input.TextArea />
      <ButtonWrapper>
        <Button style={{margin :"5px"}}type="primary">Image Upload</Button>
        <Button type="primary" htmlType="submit">
          Submit
